glsl3.vert:
precision highp float;
uniform CCGlobal {
  vec4 cc_time;

  vec4 cc_screenSize;

  vec4 cc_screenScale;

  vec4 cc_nativeSize;

  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;

  vec4 cc_exposure;

  vec4 cc_mainLitDir;

  vec4 cc_mainLitColor;

  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};

in vec3 a_position;
in vec4 a_color;
out vec4 v_color;

#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif

void main () {
  vec4 pos = vec4(a_position, 1);

  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif

  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif

  v_color = a_color;

  gl_Position = pos;
}

glsl3.frag:
precision highp float;

#if USE_ALPHA_TEST

  uniform ALPHA_TEST {
    float alphaThreshold;
  }
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

in vec4 v_color;

#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif

uniform texteffect{
  vec4 upColor;
  vec2 puv;
  vec2 charuv;
  float ualpha;
}

void main () {
  vec4 o = vec4(1, 1, 1, 1);

  #if USE_TEXTURE
  o *= texture(texture, v_uv0);
    #if CC_USE_ALPHA_ATLAS_TEXTURE
    o.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
    #endif
  #endif

  o *= v_color;

  if((puv.x-2.0*charuv.x<=v_uv0.x && v_uv0.x<=puv.x) && (v_uv0.y>=puv.y && v_uv0.y<= puv.y+charuv.y)){
      float p = max(0.0,1.0-abs(puv.x-v_uv0.x)/(2.0*charuv.x))*ualpha;
      vec4 c = upColor;
      o.r = (1.-p)*o.r/o.a+(p*c).r/o.a;
      o.g = (1.-p)*o.g/o.a+(p*c).g/o.a;
      o.b = (1.-p)*o.b/o.a+(p*c).b/o.a;
      o.r *= o.a;
      o.g *= o.a;
      o.b *= o.a;
  }

  ALPHA_TEST(o);

  gl_FragColor = o;
}

