void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

in vec4 v_color;

#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif

uniform PageCurlFrag{
  float uPosx;
  float uPosy;
  float uStartPosx;
  float uStartPosy;
  float uRadius;

  float uFlipMode;

  float uFlipY;

  sampler2D texture1;
  float uResolutionX;
  float uResolutionY;
}

vec4 getTextureColor(sampler2D textureSrc, vec2 uv){
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
  o *= texture(textureSrc, uv);
    #if CC_USE_ALPHA_ATLAS_TEXTURE
    o.a *= texture2D(textureSrc, uv + vec2(0, 0.5)).r;
    #endif
  #endif
  return o;
}

void main () {
  vec4 fragColor = vec4(1, 1, 1, 1);
  vec2 iResolution = vec2(uResolutionX, uResolutionY);

  float aspect=iResolution.x/iResolution.y;
  float radius = uRadius;

  float testMode=uFlipMode;

  vec2 uv = vec2(aspect, 1.)*v_uv0;

  vec2 maxuv = vec2(aspect, 1.0);

  if(uFlipY>0.0){
      uv.y=1.0-uv.y;
      maxuv.y=1.0-maxuv.y;
  }

  if(testMode > 0.){
    uv.x = maxuv.x - uv.x;
  }else{
    vec2 maxuv = vec2(1.0);
  }

  vec4 virtualMouse=vec4(uPosx,uPosy,uStartPosx,uStartPosy);
  vec2 mouse=virtualMouse.xy*vec2(aspect,1.)/iResolution.xy;
  vec2 mouseDir=normalize(abs(virtualMouse.zw)-virtualMouse.xy);
  vec2 origin=clamp(mouse-mouseDir*mouse.x/mouseDir.x,0.,1.);

  float mouseDist=clamp(length(mouse-origin)+(aspect-(abs(virtualMouse.z)/iResolution.x)*aspect)/mouseDir.x,0.,aspect/mouseDir.x);

  if(mouseDir.x<0.)
  {
    mouseDist=distance(mouse,origin);
  }

  float proj=dot(uv-origin,mouseDir);
  float dist=proj-mouseDist;

  vec2 linePoint=uv-dist*mouseDir;

  if(dist>radius)
  {

    if(testMode > 0.){
      uv.x = maxuv.x- uv.x;
    }
    fragColor=getTextureColor(texture1,uv*vec2(1./aspect,1.));
    fragColor.rgb*= clamp(0.9, 1.0, pow(clamp((dist-radius)*20.,0.,1.),(radius/0.04)*0.2));
    fragColor.a = 1.;
  }
  else if(dist>=0.)
  {

    float theta=asin(dist/radius);
    vec2 p2=linePoint+mouseDir*(3.14159265359-theta)*radius;
    vec2 p1=linePoint+mouseDir*theta*radius;
    if(p2.x<=aspect&&p2.y<=1.&&p2.x>0.&&p2.y>0.){
      uv = p2;

      if(testMode > 0.){
        uv = (maxuv- uv*vec2(1./aspect,1.));
        uv.y = maxuv.y- uv.y;
        uv.x = maxuv.x- uv.x;
      }else{
        uv = (1.0- uv*vec2(1./aspect,1.));
        uv.y = 1.0- uv.y;
      }
      fragColor = getTextureColor(texture1, uv);
      fragColor.rgb*=clamp(0.9, 1.0, pow(clamp((radius-dist)/radius,0.,1.),(radius/0.04)*0.2));
      fragColor.a = 1.;
    }else{

      uv = p1;
      if(testMode > 0.){
        uv.x = maxuv.x- uv.x;
      }
      fragColor = getTextureColor(texture, uv * vec2(1. / aspect, 1.));
      fragColor.rgb*=clamp(0.9, 1.0, pow(clamp((radius-dist)/radius,0.,1.),(radius/0.04)*0.2));
    }
  }
  else
  {
    vec2 p=linePoint+mouseDir*(abs(dist)+3.14159265359*radius);
    if(p.x<=aspect&&p.y<=1.&&p.x>0.&&p.y>0.&&length(mouseDir)>0.){
      uv = p ;

      if(testMode > 0.){
        uv = (maxuv - uv*vec2(1./aspect,1.));
        uv.y = maxuv.y - uv.y;
        uv.x = maxuv.x- uv.x;
      }else{
        uv = (1.0 - uv*vec2(1./aspect,1.));
        uv.y = 1.0 - uv.y;
      }
      fragColor=getTextureColor(texture1,uv);
      fragColor.a = 1.;
    }else{

      if(testMode > 0.){
        uv.x = maxuv.x- uv.x;
      }
      fragColor=getTextureColor(texture,uv*vec2(1./aspect,1.));
    }

  }

  fragColor *= v_color;

  ALPHA_TEST(fragColor);

  gl_FragColor = fragColor;
}

